void Sound_Init(void){
	Timer1_Init(&PlaySFX, period);
	Timer0_Init(&PlayBGM, period);
	
	SysTick_Init();
};

/* SFX */

/**
*PlaySFX is called by Timer1A_Handler
*plays sfx until disabled by end_SFX
*/
void PlaySFX(void){
	if(mode == 0){
		static uint32_t idx = 0;
		uint8_t modifier;
		//signed char mVal;
		modifier = getVolumeModifier();
		//mVal = (sfx[idx] - 32) * modifier;
		DAC_Out(sfx[idx++]* modifier);// + mVal);
		//turn off SFX if done playing
		if(idx >= size_SFX ) {
			end_SFX();
			idx = 0;
		}
		//test output of modifier, DAC_Out
		//ST7735_SetCursor(0, 11);
		//ST7735_OutUDec(modifier);	ST7735_OutString("    "); 
	}else{
		//play a note of x frequency until end_SFX is called
		static uint8_t idx2 = 0;
		uint8_t modifier;
		modifier = getVolumeModifier();
		DAC_Out(sineArr2[idx2] * modifier);
		idx2 = (idx2 + 1) % 32;
	}
}
/**
*Button_Press enable Timer1A
*plays Button_Press sfx until disabled by PlaySFX
*/
void Button_Press(void){
	mode = 1;	
	//TIMER1_TAILR_R = (80000000/6175)-1;
	//TIMER1_TAPR_R = 0;
	//TIMER1_CTL_R = 0x00000001; 
	//size_SFX = 1795;
	//sfx = buttonPress;
	
	//set frequency of interrupt based on note sequence
	for(uint8_t i = 0; buttonSequence[i] != null; i++){
		//play sound if sequence is not at 0
		if(buttonSequence[i] != pause){
			TIMER1_TAILR_R = buttonSequence[i]-1;
			TIMER1_TAPR_R = 0;
			TIMER1_CTL_R = 0x00000001; 
		}
		//delay for length of note then end
		for(uint32_t h = 0; h < 727240; h++); 
		end_SFX();
	}
};

/**
*Pikachu_Cry enable Timer1A
*plays Pikachu_Cry sfx until disabled by PlaySFX
*/
void Pikachu_Cry(void){
	//period = 80 000 000 / 6000
	mode = 0;	
	TIMER1_TAILR_R = (80000000/6000)-1;
	TIMER1_TAPR_R = 0;
	TIMER1_CTL_R = 0x00000001; 
	size_SFX = 4966;
	sfx = pikachuCry3;

};

/**
*Victory enable Timer1A
*plays Victory sfx until disabled by PlaySFX
*/
void Victory_SFX(void){
	mode = 1;
	
	//set frequency of interrupt based on note sequence
	for(uint8_t i = 0; victorySequence[i] != null; i++){
		//play sound if sequence is not at 0
		if(victorySequence[i] != pause){
			TIMER1_TAILR_R = victorySequence[i]-1;
			TIMER1_TAPR_R = 0;
			TIMER1_CTL_R = 0x00000001; 
		}
		//delay for length of note then end
		for(uint32_t h = 0; h < 727240; h++); 
		end_SFX();
	}
};

/**
*Loss enable Timer1A
*plays Loss sfx until disabled by PlaySFX
*/
void Loss_SFX(void){
	mode = 1;
	
	//set frequency of interrupt based on note sequence
	for(uint8_t i = 0; lossSequence[i] != null; i++){
		//play sound if sequence is not at 0
		if(lossSequence[i] != pause){
			TIMER1_TAILR_R = lossSequence[i]-1;
			TIMER1_TAPR_R = 0;
			TIMER1_CTL_R = 0x00000001; 
		}
		//delay for length of note then end
		for(uint32_t h = 0; h < 727240; h++); 
		end_SFX();
	}
};

/**
*end_SFX disables Timer1A
*/
void end_SFX(void){
	TIMER1_CTL_R = 0x00000000;
};


/* BGM */

/**
*PlayBGM is called by Timer0A_Handler
*plays bgm until disabled by end_BGM
*/
void PlayBGM(void){
	//play a note of x frequency until end_SFX is called - 
	static uint8_t idx3 = 0;
	uint8_t modifier;
	modifier = getVolumeModifier();
	DAC_Out(sineArr2[idx3] * modifier);
	idx3 = (idx3 + 1) % 32;
}	

/**
*set_BGM_1 sets bgm to battle theme
*/
void set_BGM_1(void){
	bgm = battleTheme;
}

/**
*set_BGM_2 sets bgm to title theme theme
*/
void set_BGM_2(void){
	bgm = entranceTheme;
}

/**
*end_BGM disables Timer0A
*/
void end_BGM(void){
	TIMER0_CTL_R = 0x00000000;
};

/**
*start_BGM enables Timer0A	//inactive
*/
void Start_BGM(void){
	TIMER0_CTL_R = 0x00000001;
}

/**
*Temporarily in charge of picking the notes and changing the frequency for PlayBGM
*/
void SysTick_Handler(void){
	//only play notes if BGM_on is active
	if(BGM_on == 1){
		static uint8_t BGM_idx = 0;
		//set frequency of interrupt based on note sequence
		//restart from beginning when null is reached
		if(bgm[BGM_idx] == null)
			BGM_idx = 0;
		//change sound frequency if next note to play isn't 0 or null
		if(bgm[BGM_idx] != pause){
			TIMER0_TAILR_R = bgm[BGM_idx]-1;
			TIMER0_TAPR_R = 0;
			TIMER0_CTL_R = 0x00000001; 
		}
		else
			end_BGM();
		BGM_idx ++;
	}
}

void SysTick_Init(void){
  NVIC_ST_CTRL_R = 0;                   // disable SysTick during setup
  NVIC_ST_RELOAD_R = 727240*10;						// reload value to 1ms bus clock
  NVIC_ST_CURRENT_R = 0;                // any write to current clears it
  NVIC_SYS_PRI3_R = (NVIC_SYS_PRI3_R&0x00FFFFFF)|0x40000000;                                      // enable SysTick with core clock
  NVIC_ST_CTRL_R = 0x07;//NVIC_ST_CTRL_ENABLE+NVIC_ST_CTRL_CLK_SRC;
	BGM_on = 1;
}

void Disable_BGM(void){
	BGM_on = 0;
}

void Enable_BGM(void){
	BGM_on = 1;
}

